-- Dump della struttura del database db_php
CREATE DATABASE IF NOT EXISTS `db_php` /*!40100 DEFAULT CHARACTER SET utf8mb4 COLLATE=utf8mb4_general_ci */;
USE `db_php`;

-- Dump della struttura di tabella db_php.users
CREATE TABLE IF NOT EXISTS `users` (
  `user_id` int(11) NOT NULL AUTO_INCREMENT,
  `username` varchar(50) NOT NULL,
  `password` varchar(255) NOT NULL,
  PRIMARY KEY (`user_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

-- Dump della struttura di tabella db_php.giocatori
CREATE TABLE IF NOT EXISTS `giocatori` (
  `id_giocatore` int(11) NOT NULL AUTO_INCREMENT,
  `nome` char(50) DEFAULT NULL,
  `cognome` char(50) DEFAULT NULL,
  `testo` longtext DEFAULT NULL,
  `copertina` char(50) DEFAULT NULL,
  `banner` char(50) DEFAULT NULL,
  `storico` tinytext DEFAULT NULL,
  `vittorie` int(11) DEFAULT NULL,
  `soprannome` varchar(50) DEFAULT NULL,
  `squadra` char(50) DEFAULT NULL,
  `nazionalita` char(50) DEFAULT NULL,
  PRIMARY KEY (`id_giocatore`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=14 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

-- Dump della struttura di tabella db_php.edizioni
CREATE TABLE IF NOT EXISTS `edizioni` (
  `anno` char(50) NOT NULL,
  `id_giocatore` int(11) DEFAULT NULL,
  `foto` char(50) DEFAULT NULL,
  `squadra` varchar(50) DEFAULT NULL,
  `descrizione` text DEFAULT NULL,
  PRIMARY KEY (`anno`),
  KEY `FK_edizioni_giocatori` (`id_giocatore`),
  CONSTRAINT `FK_edizioni_giocatori` FOREIGN KEY (`id_giocatore`) REFERENCES `giocatori` (`id_giocatore`) ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

-- Dump della struttura di tabella db_php.questions
CREATE TABLE IF NOT EXISTS `questions` (
  `question_id` int(11) NOT NULL AUTO_INCREMENT,
  `question_text` text NOT NULL,
  `option_a` varchar(255) NOT NULL,
  `option_b` varchar(255) NOT NULL,
  `option_c` varchar(255) NOT NULL,
  `option_d` varchar(255) NOT NULL,
  `correct_option` char(1) NOT NULL,
  PRIMARY KEY (`question_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

-- Dump della struttura di tabella db_php.scores
CREATE TABLE IF NOT EXISTS `scores` (
  `score_id` int(11) NOT NULL AUTO_INCREMENT,
  `user_id` int(11) DEFAULT NULL,
  `score` int(11) NOT NULL,
  PRIMARY KEY (`score_id`),
  KEY `user_id` (`user_id`),
  CONSTRAINT `scores_ibfk_1` FOREIGN KEY (`user_id`) REFERENCES `users` (`user_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

-- Dump dei dati della tabella db_php.giocatori: ~12 rows (circa)
INSERT INTO `giocatori` (`id_giocatore`, `nome`, `cognome`, `testo`, `copertina`, `banner`, `storico`, `vittorie`, `soprannome`, `squadra`, `nazionalita`) VALUES
    (1, 'Lionel', 'Messi', 'Lionel Andrés Messi Cuccittini, detto Leo (pronuncia spagnola [ljoˈnel ˈmesi]; Rosario, 24 giugno 1987), è un calciatore argentino, attaccante o centrocampista dell\'Inter Miami e della nazionale argentina, di cui è capitano e con cui è diventato campione del mondo nel 2022 e campione del Sud America nel 2021.\r\nSoprannominato la Pulga (in italiano "la pulce") per via della bassa statura e spesso paragonato al connazionale Diego Armando Maradona, è ritenuto uno dei più grandi calciatori di tutti i tempi.\r\nÈ il secondo marcatore più prolifico della storia del calcio e il miglior realizzatore sudamericano delle nazionali di calcio. I 44 trofei ufficiali vinti in carriera lo rendono il calciatore più titolato di sempre. Rientra, inoltre, nella lista dei calciatori con almeno 1000 presenze in carriera. Detiene il record di gol segnati in competizioni ufficiali sia in una singola stagione (82 nel 2011-2012) che in un anno solare (91 nel 2012), ed è il calciatore che detiene i record di gol (35) e assist (15) in finali di competizioni ufficiali. Figura inoltre al primo posto come miglior rifinitore della storia del calcio con più di 363 assist all\'attivo.\r\nCon 35 trofei vinti è il calciatore più decorato della storia del Barcellona, club del quale detiene anche il record di presenze, gol e assist. Insieme a Luis Suárez e Neymar ha fatto parte del tridente (chiamato MSN dalle iniziali dei cognomi) più prolifico della storia del club[28] e del calcio spagnolo. È primatista di gol e assist nella Liga, e detiene il record assoluto di gol (50 nel 2011-2012) e assist (21 nel 2019-2020)realizzati in una singola stagione nei cinque principali campionati europei.\r\nCon la nazionale argentina, di cui è primatista di presenze e reti, ha partecipato alle edizioni del campionato mondiale comprese tra il 2006 e il 2022 – record – e a sei della Copa América, tra il 2007 e il 2021.\r\nA livello individuale si è aggiudicato otto volte il Pallone d\'oro, sei volte la Scarpa d\'oro, otto volte il premio come miglior giocatore del mondo FIFA[37][38] e tre volte quello di miglior giocatore UEFA. L\'IFFHS lo ha eletto miglior calciatore del decennio 2011-2020 e per tre volte miglior marcatore internazionale dell\'anno. Inoltre la rivista inglese World Soccer lo ha eletto per sei anni World Player of The Year.\r\n\r\nNato all\'Hospital Italiano Garibaldi di Rosario da Jorge Horacio Messi, un operaio di un\'acciaieria, e da Celia María Cuccittini, donna delle pulizie, Lionel Messi è di origini italiane: il trisavolo paterno Angelo Messi è migrato da Recanati nel 1883, e il trisavolo materno Raniero Coccettini è migrato da San Severino Marche nel 1899.Inoltre è un lontano parente dell\'ex pugile italiano Luca Messi, con cui condivide le origini familiari.È sposato dal 2017 con Antonella Roccuzzo dalla quale ha avuto tre figli. Ha due fratelli maggiori, Rodrigo e Matías, e una sorella minore, María Sol, ed è cugino dei calciatori Maxi ed Emanuel Biancucchi. Nell\'ottobre del 2011, il quotidiano di Lleida Segre ha inoltre rivelato una probabile parentela tra l\'argentino e Bojan Krkić, che sarebbero cugini di 4º grado. Nel 2005 ha ottenuto la cittadinanza spagnola, divenendo cittadino comunitario. Ha studiato presso la scuola elementare N° 66 "Gral. Las Heras".\r\nMolto impegnato nel sociale, nel 2007 ha dato vita alla Fundación Leo Messi, una fondazione che si occupa di garantire sostegno, soprattutto medico, ai bambini indigenti. L\'11 marzo 2010 è stato nominato ambasciatore UNICEF. Si è distinto per numerose iniziative umanitarie.\r\nÈ ritratto sulla copertina di numerose edizioni delle serie di videogiochi PES (2009-2011) e FIFA (2012-2016), ed è sponsorizzato dall\'azienda tedesca Adidas, per la quale ha anche realizzato diversi spot pubblicitari. Nel giugno 2010 ha siglato un contratto di tre anni con Herbalife, che garantisce un prezioso sostegno alla Fundación Leo Messi.Nell\'aprile 2011, Messi è stato inserito tra i nomi papabili per vincere l\'annuale classifica del Time che premia il personaggio più influente dell\'anno. Nel luglio 2011 è stato dichiarato "Patrimonio storico-sportivo dell\'umanità" insieme a Pelé e al messicano Hugo Sánchez. Nel dicembre dello stesso anno è nominato "Cittadino illustre" e "Ambasciatore" della sua città natale, Rosario.\r\nIl 2 luglio 2014 è presentato in anteprima a Rio de Janeiro il primo film dedicato interamente alla vita di Messi. A dirigere il film biografico è stato lo spagnolo Álex de la Iglesia, e la pellicola è stata realizzata utilizzando immagini esclusive che ritraggono anche il Messi bambino, oltre ai suoi gol migliori e i trofei vinti.Secondo la rivista statunitense Forbes, è stato il calciatore più pagato al mondo dal 2017 al 2019 e dal 2021 al 2022, raggiungendo l\'apice come sportivo più pagato in assoluto negli anni 2019[61] e 2022. Sempre dalla stessa rivista, con un guadagno complessivo di 750 milioni di euro nel decennio compreso tra il 2010 e il 2019, Messi è stato classificato terzo nella lista degli sportivi più pagati del decennio.\r\n', 'messi.jpg', NULL, NULL, 8, 'La Pulga', NULL, 'Argentina'),
	(2, 'Cristiano', 'Ronaldo', 'Cristiano Ronaldo dos Santos Aveiro, meglio noto come Cristiano Ronaldo (pronuncia portoghese [kɾɨʃˈtjɐnu ʁuˈnaɫdu]; Funchal, 5 febbraio 1985), è un calciatore portoghese, attaccante dell\'Al-Nassr e della nazionale portoghese, di cui è capitano e con cui è diventato campione d\'Europa nel 2016 e ha vinto la UEFA Nations League 2018-2019. Soprannominato CR7 per via del numero di maglia che lo ha contraddistinto per gran parte della sua carriera, è ritenuto uno dei migliori calciatori di tutti i tempi. È inoltre il marcatore più prolifico nella storia del calcio, nonché il detentore dei primati di presenze e di reti nelle nazionali di calcio. Rientra nella ristretta cerchia dei calciatori con almeno 1000 presenze in carriera, nella quale risulta essere l\'attaccante con il maggior numero di apparizioni. Nel corso della sua carriera ha indossato le maglie di Sporting Lisbona, Manchester United, Real Madrid, Juventus e Al-Nassr, vincendo a livello internazionale cinque UEFA Champions League, due Supercoppe UEFA, quattro Coppe del mondo per club FIFA e una Coppa dei Campioni araba. Detiene il primato di presenze, di reti e di assist nella UEFA Champions League e nelle competizioni UEFA per club.\r\nCon la nazionale portoghese, della quale è primatista di presenze e di reti, ha preso parte a cinque edizioni del campionato mondiale, arrivando al quarto posto nel 2006; a cinque edizioni del campionato d\'Europa, vincendo quello del 2016 (primo titolo per il Portogallo) e arrivando al secondo posto nel 2004 e al terzo posto nel 2012; a un\'edizione della Confederations Cup (2017), classificandosi al terzo posto; e alla fase finale di una UEFA Nations League (2018-2019), vincendo anche quest\'ultima. Al campionato europeo detiene il record di reti e di presenze ed è l\'unico calciatore sceso in campo ed andato a segno per cinque edizioni (nel 2012 e nel 2020 ottenne anche il titolo di capocannoniere). Ai Mondiali è l\'unico calciatore andato a segno in cinque edizioni.\r\nÈ comparso ininterrottamente nella classifica del Pallone d\'oro dal 2004 al 2022, vincendolo per cinque volte,[22] e ha ottenuto quattro Scarpe d\'oro. Per cinque volte si è aggiudicato anche il premio come migliore giocatore al mondo FIFA e per quattro volte quello come miglior giocatore UEFA. È stato inoltre nominato cinque volte miglior marcatore internazionale dell\'anno IFFHS. La stessa IFFHS lo ha eletto miglior marcatore del mondo della decade 2011-2020. La rivista inglese World Soccer lo ha eletto per 5 anni World Player of The Year.\r\nVincitore di numerosi premi accessori, dal 2007 al 2020 ha fatto sempre parte della FIFA FIFPro World XI, è stato inserito 9 volte nella squadra dell\'anno ESM, in 15 occasioni nella squadra dell\'anno UEFA, in 11 volte in quella dell\'Équipe e in 5 volte in quella dell\'IFFHS. La rivista francese France Football lo ha inserito nella "squadra ideale degli anni 2010" e nel 2020 lo colloca nel Dream Team del Pallone d\'oro come migliore ala sinistra della storia del calcio. Nel 2009 il Sun e Sports Illustrated lo inseriscono nella loro "squadra ideale del decennio 2000-2009" mentre l\'IFFHS lo colloca nella "squadra del decennio 2011-2020". È stato inserito per 3 volte nell\'All Star Team degli Europei.\r\n\r\nÈ nato nel 1985 a Funchal da Maria Dolores dos Santos, una cuoca, e José Dinis Aveiro, un giardiniere municipale con origini capoverdiane (la nonna era originaria di São Vicente), in un momento nel quale la famiglia versava in una condizione di grave disagio economico a causa dei problemi di alcolismo del padre (che lo portarono alla morte nel 2005),[30] con la madre inizialmente decisa ad abortire.\r\nIl nome Cristiano è dovuto alla fede cristiana della madre, mentre il secondo nome, Ronaldo, fu scelto in onore di Ronald Reagan, attore preferito del padre e presidente degli Stati Uniti a quell\'epoca. Ha un fratello maggiore, Hugo (nato nel 1975), e due sorelle maggiori, Elma (nata nel 1974) e Liliana Cátia (nata nel 1976).\r\nAppassionato di calcio fin dall\'infanzia, all\'età di 14 anni decide congiuntamente alla famiglia di lasciare i propri studi per dedicarsi allo sport. Nel mentre, un anno dopo, gli viene diagnosticata una forma di tachicardia che lo costringe, inizialmente, a smettere di perseguire il suo sogno. Ha successivamente subito un intervento con il laser per cauterizzare più vie cardiache in una sola, al fine di alterare la sua frequenza cardiaca a riposo;[39] permettendogli di tornare in poco tempo a praticare il calcio.\r\nA seguito della morte del padre il 7 settembre 2005, ha deciso di non bere alcolici per non seguire la sua sorte. Il corpo di Ronaldo è inoltre privo di alcun tatuaggio poiché ha dato disponibilità come donatore di midollo osseo e dona regolarmente sangue, motivo per cui nel 2018 ha anche fatto da testimonial per l\'associazione AVIS.\r\nNel 2007, viene diagnosticato un cancro al seno alla madre; risolto il problema, a febbraio del 2019, la madre ha annunciato alla stampa di avere di nuovo un tumore. A marzo del 2020, è stata ricoverata in terapia intensiva a seguito di un ictus da cui si è successivamente ripresa.\r\n', 'ronaldo.jpg', NULL, NULL, 5, 'CR7', NULL, 'Portogallo'),
	(3, 'Karim', 'Benzema', 'Karim Mostafa Benzema (Lione, 19 dicembre 1987) è un calciatore francese, attaccante dell\'Al-Ittihād. Con la nazionale francese è diventato vicecampione del mondo nel 2022 e ha vinto la UEFA Nations League 2020-2021. Considerato uno dei migliori giocatori della sua generazione, è cresciuto nelle giovanili dell\'Olympique Lione, con cui ha esordito in prima squadra nel 2004 e si è affermato, vincendo quattro campionati francesi consecutivi (dal 2005 al 2008), due Supercoppe francesi e una Coppa di Francia. Trasferitosi al Real Madrid nel 2009, si è consacrato come uno dei migliori attaccanti del panorama internazionale. \r\nCon il club madrileno, in cui ha militato sino al 2023, ha arricchito il proprio palmarès, vincendo quattro campionati, tre Coppe del Re, quattro Supercoppe spagnole, cinque UEFA Champions League, quattro Supercoppe UEFA e cinque Coppa del mondo per club FIFA.\r\nNel 2004 ha vinto il campionato europeo di categoria con la nazionale francese Under-17. Con la nazionale maggiore, nella quale ha debuttato nel 2007, ha giocato tre campionati europei (2008, 2012 e 2020) e un campionato del mondo (2014). Nel 2015, in seguito ad una vicenda giudiziaria che l\'ha visto coinvolto, è stato escluso dalla nazionale, prima di farvi ritorno nel 2021, anno in cui si è aggiudicato la UEFA Nations League. Nel 2022 è stato convocato per il campionato mondiale, ma non ha collezionato alcuna presenza per infortunio. Nello stesso anno ha lasciato la selezione francese.\r\nA livello individuale, ha vinto il Pallone d\'oro nel 2022, diventando il quinto francese ad aggiudicarsi il riconoscimento. Inoltre, è stato nominato una volta Giocatore dell\'anno UEFA (2021-2022) e per quattro volte Calciatore francese dell\'anno, oltre a vincere il Trofeo Bravo e il Trofeo Alfredo Di Stéfano. È stato capocannoniere della Ligue 1 (2007-2008), della Liga (2021-2022) e della UEFA Champions League (2021-2022). Con 32 trofei ufficiali vinti tra club e nazionale, è il calciatore francese più titolato, nonché il migliore marcatore francese nelle competizioni UEFA per club e nel campionato spagnolo.\r\n\r\nÈ nato a Lione, nella regione meridionale dell\'Alvernia-Rodano-Alpi, da Hafid Benzema, cabilo originario di Tigzirt, emigrato in Francia da ragazzo nel corso degli anni cinquanta, e da Wahida Djebbara, francese figlia di immigrati algerini originari di Orano. È cresciuto tra Lione e Bron, nel quartiere di Terraillon.', 'benzema.jpg', NULL, NULL, 1, 'The Dream', NULL, 'Francia'),
	(4, 'Luka', 'Modric', 'Luka Modrić (Zara, 9 settembre 1985) è un calciatore croato, centrocampista del Real Madrid, di cui è vice capitano e della nazionale croata, di cui è capitano e con cui è stato vicecampione del mondo nel 2018. Considerato uno dei migliori centrocampisti della storia del calcio, nel 2020 è stato eletto dalla rivista Sportske novosti migliore calciatore croato di sempre. \r\nNel corso della sua carriera ha vestito le maglie di Dinamo Zagabria, Tottenham e Real Madrid vincendo tre campionati croati consecutivi (2005-2006, 2006-2007, 2007-2008), due Coppe di Croazia (2006-2007, 2007-2008), una Supercoppa di Croazia (2006), tre campionati spagnoli (2016-2017, 2019-2020, 2021-2022), due Coppe del Re (2013-2014, 2022-2023), quattro Supercoppe di Spagna (2012, 2017, 2020, 2022), cinque UEFA Champions League (2013-2014, 2015-2016, 2016-2017, 2017-2018, 2021-2022), quattro Supercoppe UEFA (2014, 2016, 2017, 2022) e cinque Coppe del mondo per club FIFA (2014, 2016, 2017, 2018, 2022). \r\nDal 2006 fa parte della nazionale croata, della quale è il primatista di presenze e con cui ha partecipato a quattro campionati del mondo (2006, 2014, 2018 e 2022, classificandosi al secondo posto nell\'edizione del 2018 e al terzo posto in quella del 2022) e a quattro campionati d\'Europa (2008, 2012, 2016, 2020). Con la nazionale croata ha ottenuto il secondo posto nella UEFA Nations League 2022-2023. È il calciatore europeo con piu presenze in una nazionale a non aver vinto alcun titolo, oltre ad essere quello che ha partecipato a più fasi finali dei tornei ( 4 mondiali, 4 europei e 1 Nations League ), senza essere mai riuscito ad alzare la coppa. \r\nA livello individuale si è consacrato nel 2018, vincendo il Pallone d\'oro, il Best FIFA Men\'s Player, il UEFA Men\'s Player of the Year e il premio come miglior giocatore del campionato del mondo. È stato inoltre eletto una volta migliore giocatore della Coppa del mondo per club (2017) e due volte miglior centrocampista della UEFA Champions League (2017 e 2018) e inserito nella squadra della stagione della UEFA Champions League in 6 occasioni (2013-2014, 2015-2016, 2016-2017, 2017-2018, 2020-2021 e 2021-2022). Detiene il record di vittorie del premio di calciatore croato dell\'anno (11)[8] e rientra altresì nella ristretta cerchia dei calciatori con almeno 1000 presenze in carriera.giudicato otto volte il Pallone d\'oro,[36] sei volte la Scarpa d\'oro, otto volte il premio come miglior giocatore del mondo FIFA[37][38] e tre volte quello di miglior giocatore UEFA.[39] L\'IFFHS lo ha eletto miglior calciatore del decennio 2011-2020 e per tre volte miglior marcatore internazionale dell\'anno. Inoltre la rivista inglese World Soccer lo ha eletto per sei anni World Player of The Year.\r\n\r\nLuka Modrić è nato il 9 settembre 1985 nella frazione di Modrići a Zaton Obrovaćki, un piccolo villaggio situato alle pendici meridionali del monte Velebit, nella Dalmazia centrale, nell\'epoca in cui la Croazia era ancora un\'unità territoriale della Jugoslavia. È il figlio maggiore di Radojka Dopuđ, sindacalista originaria di Kruševo, frazione di Obrovac, e dell\'aeromeccanico Stipe Modrić, dapprima entrambi impiegati come operai tessili all\'interno di un maglificio, e con cui trascorse i suoi primi anni di vita all\'interno di una casa cantoniera, spesso riempendo le sue giornate aiutando suo nonno nell\'allevamento del bestiame.', 'modric.jpg', NULL, NULL, 1, 'Maestro', NULL, 'Croazia'),
	(5, 'Ronaldo', 'Nazario', 'Ronaldo Luís Nazário de Lima, conosciuto semplicemente come Ronaldo (AFI: [xo\'nawdʊ lu\'iz na\'zaɾjʊ dʒi \'limɐ]; Rio de Janeiro, 18 settembre 1976), è un dirigente sportivo ed ex calciatore brasiliano, di ruolo attaccante, proprietario del Real Valladolid e del Cruzeiro.Soprannominato O Fenômeno (in italiano "Il Fenomeno"), è considerato da molti il più forte attaccante della sua generazione, nonché uno dei migliori calciatori della storia. Cresciuto nel São Cristóvão, squadra minore di Rio de Janeiro, viene notato da Jairzinho, che lo porta al Cruzeiro dove esordisce in massima serie e vince una Coppa del Brasile e un campionato Mineiro. \r\nArriva in Europa nel 1994 grazie al PSV, con il quale conquista una Coppa dei Paesi Bassi. Nel 1996 si trasferisce al Barcellona, con cui vince una Supercoppa spagnola, una Coppa delle Coppe e una Coppa di Spagna. Dopo una sola stagione viene acquistato dall\'Inter, dove resta cinque stagioni trionfando in Coppa UEFA. Nell\'estate del 2002 passa al Real Madrid e in cinque anni vince un campionato spagnolo, una Supercoppa spagnola e una Coppa Intercontinentale. Conclusa l\'esperienza in Spagna, nel 2007 si trasferisce al Milan, prima di chiudere la carriera nel 2011 con il Corinthians vincendo un campionato Paulista e una Coppa del Brasile.\r\nIn nazionale si è aggiudicato due campionati del mondo (Stati Uniti 1994 e Corea del Sud-Giappone 2002), due Coppe America (Bolivia 1997 e Paraguay 1999) e una Confederations Cup (1997). Del mondiale nippo-coreano, che lui stesso decise grazie a una doppietta personale nella finale, fu capocannoniere e venne nominato secondo miglior giocatore dietro al portiere tedesco Oliver Kahn,e fu incluso nella squadra ideale del torneo. Fino all\'edizione del 2014 ha detenuto il titolo di miglior marcatore della storia del campionato del mondo con quindici gol complessivi (quattro nel 1998, otto nel 2002 e tre nel 2006), prima di essere superato da Miroslav Klose.\r\nA livello individuale ha vinto due edizioni del Pallone d\'oro (1997 e 2002) e tre del FIFA World Player of the Year (1996, 1997 e 2002) e assieme a Zinédine Zidane, è il giocatore ad aver vinto più volte quest\'ultimo premio. Inserito da Pelé nel marzo 2004 nella FIFA 100, la lista dei 125 migliori calciatori viventi, si è classificato in 13ª posizione nella lista dei cento migliori giocatori del XX secolo redatta nel 1999 da World Soccer che nel 2013 lo inserisce nella migliore formazione calcistica di tutti i tempi. Il 9 marzo 2018, in concomitanza col 110º anniversario della fondazione dell\'Inter, è stato il primo attaccante inserito nella Hall of Fame del club milanese. Il 14 dicembre 2020 è stato selezionato dalla rivista France Football nel Dream Team del Pallone d\'oro in qualità di migliore attaccante centrale della storia del calcio.\r\nLa sua carriera è stata condizionata da gravi infortuni, soprattutto alle ginocchia, e, nella fase finale, da evidenti problemi di peso: sebbene il giocatore sia riuscito a mantenere prestazioni di alto livello anche dopo aver perso parte della sua velocità e mobilità, è opinione diffusa che senza i suddetti guai fisici la considerazione nei suoi confronti sarebbe stata ancora più alta, e che il suo rendimento prima delle operazioni alle ginocchia non avrebbe avuto eguali nella storia del calcio.\r\n\r\nTerzo figlio di Nélio Nazário de Lima e Sônia dos Santos Barata, Ronaldo Luís Nazário de Lima nacque il 18 settembre 1976 nel quartiere di Bento Ribeiro, nella zona nord-ovest di Rio de Janeiro; i genitori lo chiamarono "Ronaldo" in onore del medico Ronaldo Valente, che fece nascere il bambino. È stato sposato dapprima con la modella Milene Domingues, che il 6 aprile 2000 ha dato alla luce Ronald, poi con Daniella Cicarelli e in seguito è convolato a nozze con Maria Beatriz Anthony, dalla quale ha avuto due bambine. Nel dicembre del 2012 è stata resa nota la separazione dalla terza moglie. Nel dicembre del 2010, a cinque anni di distanza, ha riconosciuto Alex, quello che nei fatti è il suo secondo figlio, nato da una relazione con la modella Michele Umezu. Pochi giorni dopo ha dichiarato di essersi sottoposto a un\'operazione di vasectomia per evitare di avere altri figli in futuro. Nel novembre del 2005 ha acquisito, oltre a quella brasiliana, anche la cittadinanza spagnola.', 'fenomeno2.jpg', 'fenomeno.jpg', 'si', 2, 'Il Fenomeno', NULL, ''),
	(6, 'Michel', 'Platini', 'Michel François Platini (Jœuf, 21 giugno 1955) è un dirigente sportivo, allenatore di calcio ed ex calciatore francese, di ruolo centrocampista. Considerato uno dei più forti calciatori della storia del calcio, occupa la 5ª posizione nella speciale classifica dei migliori calciatori del XX secolo pubblicata dalla rivista World Soccer e la 7ª posizione nell\'omonima lista stilata dall\'IFFHS. La FIFA lo ha inoltre piazzato al quinto posto nella classifica dei migliori numeri dieci della storia del calcio mentre nel marzo del 2004, Pelé lo ha inserito nella FIFA 100, la lista dei 125 migliori calciatori viventi, redatta in occasione del Centenario della FIFA.\r\nHa vinto consecutivamente tre Palloni d\'oro assegnati da France Football e viene considerato da numerosi addetti ai lavori come il più rappresentativo giocatore francese del Novecento, davanti a Zinédine Zidane e Raymond Kopa. Nel 2002 fu incluso nel FIFA World Cup Dream Team, selezione formata dai migliori undici giocatori della storia dei Mondiali, mentre nel 2011 entra nella Hall of Fame del calcio italiano tra i giocatori stranieri.\r\nLegò il suo nome a quello del Nancy e del Saint-Étienne, divenendo un giocatore-simbolo per entrambi i club, nonché della Juventus e della nazionale francese, di cui è stato tra i principali artefici dei rispettivi successi degli anni 1980. Terminò la carriera a 31 anni, dopo aver conquistato numerosi trofei tra cui un campionato francese, due campionati italiani e, ad eccezione della Coppa UEFA, pressoché tutte le competizioni confederali dell\'epoca, divenendo al contempo capocannoniere della Serie A per tre volte consecutive, dal 1983 al 1985. \r\nCon la nazionale transalpina ottenne piazzamenti di rilievo nelle competizioni internazionali del decennio 1976-86, tra cui la vittoria del campionato d\'Europa 1984 (dove inoltre si aggiudicò la classifica marcatori con 9 reti, che ne fanno il migliore realizzatore in una singola edizione del torneo), e il terzo posto al campionato del mondo 1986.\r\nNella sua lunga carriera da calciatore, in cui disputò oltre 650 partite tra club e rappresentativa nazionale, spiccò la completa assenza di espulsioni: un fatto più unico che raro alla luce dell\'aggressività delle marcature nei confronti dei giocatori più creativi, tra gli anni 1970 e 1980. È stato co-presidente del comitato organizzatore del campionato del mondo 1998, e presidente dell\'UEFA dal 2007 al 2016, succedendo a Lennart Johansson.\r\n\r\nPlatini nacque in rue Saint-Exupéry a Jœuf, un comune francese situato nel dipartimento della Meurthe e Mosella, nella regione della Lorena. Nella sua famiglia, originaria di Agrate Conturbia, in Piemonte, e della provincia di Belluno da parte di madre, era radicata la cultura sportiva: il nonno, Francesco Platìni, era un muratore italiano che era emigrato oltralpe divenendo ristoratore, gestendo un bar battezzato proprio da lui le Café des Sports, mentre il padre, Aldo, era un professore di matematica che nel tempo libero era diventato prima capitano della squadra del Jovincenne e poi allenatore del Nancy, all\'epoca in terza divisione. La madre, Anna Piccinelli, serviva liquori nel locale del suocero; ha inoltre una sorella, Martina. Il suo cognome viene solitamente pronunciato alla francese, cioè con accento sulla seconda "i"; nella pronuncia italiana l\'accento è sulla prima "i".\r\nAll\'età di sette anni Michel iniziò a tirar calci al pallone, allenandosi con il cane di sua cugina Stefanina. Da bambino era soprannominato ratz ("rasoterra" in dialetto lorenese) e le nain ("nano" in francese) per via della sua bassa statura. Il suo idolo sportivo d\'infanzia era Pelé e per questo, ogni volta che gli capitava di dover firmare un pezzo di carta, si divertiva a storpiare il proprio cognome in "Peleatini". Oltre all\'iniziale bassa statura cui si accennava, in giovane età a Michel vennero riscontrati in più occasioni problemi fisici, tra cui una capacità polmonare molto limitata e un\'insufficienza cardiaca.Il 27 dicembre 1977 sposò una studentessa di economia, Christelle, come lui figlia di genitori italiani; dal loro matrimonio nasceranno due figli: Laurent, avvocato della Qatar Sports Investment che controlla il Paris Saint-Germain, e Marine, attrice, entrambi scampati a un rapimento quando erano ancora bambini.', 'platini2.jpg', 'patini.jpg', 'si', 3, 'Le Roi', NULL, ''),
	(7, 'Marco', 'Van Basten', 'Marcel van Basten, detto Marco (Utrecht, 31 ottobre 1964), è un allenatore di calcio ed ex calciatore olandese, di ruolo attaccante. Ritenuto uno dei migliori calciatori di tutti i tempi, ha segnato 314 gol (dei quali 277 con le squadre di club, 24 con la nazionale olandese e 13 con la nazionale Under-21) in carriera, conclusasi prematuramente all\'età di soli 28 anni per via di alcuni infortuni; anche se ufficialmente l\'annuncio del suo ritiro fu dato nel 1995, quando il giocatore aveva 30 anni. \r\nEmblema dell\'Ajax e del Milan, in maglia rossonera ha composto un eccezionale trio assieme ai connazionali Ruud Gullit e Frank Rijkaard, simbolo di numerosi successi della squadra allenata da Arrigo Sacchi e poi da Fabio Capello. Campione d\'Europa con i Paesi Bassi nel 1988, torneo in cui fu anche capocannoniere, van Basten vinse tre Palloni d\'oro, nel 1988, 1989 e 1992. \r\nFu eletto FIFA World Player nel 1992 e occupa la 9ª posizione nella speciale classifica dei migliori calciatori del XX secolo pubblicata dalla rivista World Soccer e la 12ª posizione nell\'omonima classifica stilata dall\'IFFHS. Nel marzo del 2004, Pelé lo ha anche inserito nella FIFA 100, la lista dei 125 migliori calciatori viventi, redatta in occasione del Centenario della FIFA.\r\nÈ stato il primo dei dieci calciatori a segnare una quaterna in una sola partita di UEFA Champions League, oltreché l\'unico (insieme a Silvio Piola) ad avere siglato almeno un gol a ogni squadra di Serie A da lui affrontata.Durante i festeggiamenti per il centenario del Milan, nel dicembre 1999, Van Basten è stato eletto "attaccante milanista del secolo". È ambasciatore del calcio per la UEFA, dopo aver già ricoperto il ruolo di Chief Officer for Technical Development per la FIFA.\r\n\r\nDalla moglie Liesbeth ha avuto tre figli, Rebecca, Angela e Alexander. Pratica il golf come hobby. Nel febbraio del 2023 apre un ristorante italiano ad Utrecht insieme al suo ex compagno di squadra Frank Rijkaard.', 'VanBasten2.jpeg', 'VanBasten.png', 'si', 3, 'Il cigno di Utrecht', NULL, ''),
	(8, 'Kevin', 'Keegan', 'Joseph Kevin Keegan, noto semplicemente come Kevin Keegan, (in inglese: \'kɛvɪn \'ki:gan; Armthorpe, 14 febbraio 1951) è un ex allenatore di calcio ed ex calciatore inglese, di ruolo attaccante. Considerato come uno dei più forti calciatori inglesi della storia del calcio, occupa la 56ª posizione nella speciale classifica dei migliori calciatori del XX secolo pubblicata dalla rivista World Soccer e la 38ª posizione nell\'omonima classifica stilata dall\'IFFHS. Nel marzo del 2004, Pelé lo ha anche inserito nella FIFA 100, la lista dei 125 migliori calciatori viventi, redatta in occasione del Centenario della FIFA. \r\nVincitore del Pallone d\'oro per due anni di seguito (1978 e 1979), è stato inserito in totale per quattro volte tra i candidati alla vittoria del premio arrivando a raggiungere anche la seconda posizione nel 1977. Ha raggiunto i maggiori successi negli anni in cui militò nel Liverpool, vincendo, tra il resto, tre volte il campionato inglese, due volte la Coppa Uefa ed una volta la Coppa dei Campioni. Militò inoltre anche nell\'Amburgo, dove vinse un campionato tedesco.\r\n\r\nAll\'età di ventitré anni, il 23 settembre 1974 si è sposato con Jean, da cui ha avuto le figlie Laura e Sarah.', 'keegan2.jpg', 'keegan.jpg', 'si', 2, 'Mighty Mouse', NULL, ''),
	(9, 'Franz', 'Beckenbauer', 'Franz Anton Beckenbauer (AFI: [ˈfʁants ˈantoːn ˈbɛkŋ̍ˌbaʊɐ]; Monaco di Baviera, 11 settembre 1945 – Salisburgo, 7 gennaio 2024) è stato un calciatore, allenatore di calcio e dirigente sportivo tedesco, di ruolo difensore. Soprannominato Kaiser, è considerato uno dei più grandi giocatori della storia del calcio. Occupa la 4ª posizione nella speciale classifica dei migliori calciatori del XX secolo pubblicata dalla rivista World Soccer e la 3ª posizione nell\'omonima lista pubblicata dall\'IFFHS. Nel marzo del 2004, Pelé lo ha anche inserito nella FIFA 100, la lista dei 125 migliori calciatori viventi, redatta in occasione del Centenario della FIFA. \r\nA livello individuale ha ottenuto inoltre per due volte il Pallone d\'oro, nel 1972 e nel 1976, divenendo il primo difensore ad essere insignito per due volte dell\'ambìto premio. Nel dicembre 2020 è inoltre stato inserito nel Dream Team del Pallone d\'oro in qualità di miglior difensore centrale di tutti i tempi.\r\nÈ cresciuto nel Bayern Monaco, al quale ha legato gran parte della propria carriera (dal 1964 al 1977) vincendo quattro Coppe di Germania, quattro campionati, una Coppa delle Coppe, tre Coppe dei Campioni e una Coppa Intercontinentale. Nel 1977 si trasferisce ai N.Y. Cosmos, con i quali in quattro stagioni si aggiudica tre campionati nordamericani. Dal 1980 al 1982 milita nell\'Amburgo, dove vince un altro campionato prima di chiudere la carriera ancora con i N.Y. Cosmos.\r\nCon la Germania Ovest ha partecipato a tre campionati del mondo (1966, 1970, 1974) e due campionati d\'Europa (1972, 1976), vincendo da protagonista l\'europeo 1972 e il mondiale 1974. Vanta inoltre una piazza d\'onore ai Mondiali (1966) e una agli Europei (1976), oltre al terzo posto ai mondiali messicani del 1970; in quest\'ultima occasione ha inoltre preso parte alla cosiddetta partita del secolo.\r\nDa allenatore ha guidato la Germania Ovest, l\'Olympique Marsiglia e il Bayern Monaco (in due occasioni), vincendo il campionato del mondo 1990, il campionato tedesco 1993-1994 e la Coppa UEFA 1995-1996. Assieme a Mário Zagallo e Didier Deschamps è una delle sole tre personalità del mondo del calcio riuscite a vincere il mondiale sia da giocatori sia da allenatori (e, come Deschamps, da capitano della nazionale vincente). Da commissario tecnico ha inoltre raggiunto la finale del mondiale 1986, uscendone tuttavia sconfitto dall\'Argentina trascinata da Diego Armando Maradona.\r\nDal 2009 fino alla morte, avvenuta nel 2024, è stato presidente onorario del Bayern Monaco. È stato anche presidente del comitato organizzatore del campionato del mondo del 2006, venendo però criticato per le strategie adottate al fine di assicurare alla Germania l\'organizzazione di tale edizione\r\n\r\nFranz Beckenbauer è nato nel 1945 come secondo figlio del segretario postale Franz Beckenbauer senior (1905-1977) e di sua moglie Antonie (1913-2006), nata Hupfauf, a Monaco di Baviera nel quartiere di Giesing e crebbe con il fratello Walter di quattro anni. Anche suo zio, Alfons Beckenbauer, ha militato nel Bayern Monaco. Beckenbauer ha vissuto a Grünwald negli anni \'70. Nel 1977 ha trasferito la sua residenza a Sarnen, nel cantone svizzero di Obvaldo. Dal 1982 ha vissuto a Oberndorf in Tirol in Austria, e dal 2005 a Salisburgo, mentre lavorava ancora in Germania.È stato sposato tre volte e ha avuto cinque figli, tra cui Stephan, calciatore professionista, deceduto nel 2015. È morto il 7 gennaio 2024, all\'età di 78 anni, come annunciato dalla sua famiglia all\'agenzia stampa DPA. I funerali sono stati celebrati in forma privata mentre il 19 gennaio si è svolta all\'Allianz Arena una cerimonia commemorativa.', 'becnabor2.jpg', 'becnabor.png', 'si', 2, 'Der Kaiser', NULL, ''),
	(10, 'Alfredo', 'Di Stefano', 'Alfredo Di Stéfano Laulhé ([alˈfɾeðo ði esˈtefano]; Barracas, 4 luglio 1926 – Madrid, 7 luglio 2014) è stato un calciatore e allenatore di calcio argentino naturalizzato spagnolo, di ruolo attaccante.Soprannominato Saeta Rubia (Freccia Bionda) o Don Alfredo, è unanimemente ritenuto tra i più grandi calciatori di tutti i tempi. Occupa la 6ª posizione nella speciale classifica dei migliori calciatori del XX secolo pubblicata dalla rivista World Soccer e la 4ª posizione nell\'omonima lista stilata dall\'IFFHS, qui dietro solamente a Pelè, Cruijff e Beckenbauer. Nel mese di novembre del 2003 venne insignito del titolo onorifico di Golden Player della Spagna come miglior giocatore spagnolo dell\'ultimo mezzo secolo (1954-2003). Nel marzo del 2004, Pelé lo ha anche inserito nella FIFA 100, la lista dei 125 migliori calciatori viventi, redatta in occasione del Centenario della FIFA.\r\nMeglio noto per i suoi successi con il Real Madrid, squadra di cui è considerato una bandiera, con 308 reti è stato per molti anni il miglior marcatore della storia fino all\'arrivo di Raúl. Con il club ha inoltre largamente dominato in Liga e Coppa Campioni, vincendone cinque edizioni consecutive (assieme a Francisco Gento e José María Zárraga) e andando a segno in tutte le finali, unico a riuscirci. Ha giocato con le nazionali di Argentina e Spagna, vincendo il Sudamericano 1947 e venendo convocato ai Mondiali del 1962 con gli iberici, senza giocare a causa di un infortunio.\r\nDue volte Pallone d\'oro (1957 e 1959) nonché unico calciatore a vincere il "Super Pallone d\'oro" nel 1989, rientra nel novero dei giocatori capaci di laurearsi capocannonieri in tre differenti paesi. Ha perso il primato di miglior marcatore nella storia del Clásico per opera di Lionel Messi, ma con 18 realizzazioni rimane il madridista ad aver segnato più gol ai rivali blaugrana assieme a Cristiano Ronaldo.È sesto nella classifica dei migliori marcatori del campionato spagnolo, con 216 reti in 282 partite tra il 1953 e il 1964. \r\nDa allenatore, ha guidato il Valencia alla vittoria in campionato (1971) e in Coppa delle Coppe (1980) ed è stato l\'unico campione argentino ad aver allenato sia il River Plate sia il Boca Juniors, vincendo con entrambe le formazioni. \r\n\r\nFiglio di Eulalia Laulhé Gilmont, argentina di origini francesi (Béarn) e irlandesi, e di Alfredo Di Stefano, italo-argentino nato a Capri ma di origini lombardo-sicule e a sua volta figlio del siciliano Michele Di Stefano, di Nicolosi (Catania), Alfredo nasce a Barracas, uno dei barrios di Buenos Aires, da una famiglia borghese.Aveva un fratello, Tulio, e una sorella, Norma, che giocava a pallacanestro.Alfredo Di Stéfano sposa Sara Frietes Varela nel 1950. La coppia ha sei figli: Nanette, Silvana, Alfredo, Elena, Ignacio e Sofia. Dopo aver concluso la carriera sportiva, continua a vivere in Spagna, vicino allo stadio Santiago Bernabéu, facendosi mettere nel giardino di casa una statua raffigurante un pallone con inciso la scritta Gracias vieja. Il 24 dicembre 2005 è colto da infarto, riuscendo a sopravvivere nelle cliniche di Sagunto e Valencia. Nel corso della sua vita ha preso parte a diverse pellicole, tra cui anche l\'autobiografica Saeta rubia (1956), dove interpreta sé stesso. Ha scritto un\'autobiografia, Gracias vieja, pubblicata nel 2000.\r\nÈ morto il 7 luglio 2014, pochi giorni dopo il suo ottantottesimo compleanno, all\'ospedale "Gregorio Marañón" di Madrid dopo essere stato colpito da un infarto due giorni prima mentre passeggiava nei pressi dello Stadio Bernabéu. Di Stéfano soffriva di problemi di cuore dal 2005, quando a Valencia aveva subito un primo infarto col conseguente impianto di quattro bypass.', 'distefano2.jpg', 'distefano.jpg', 'si', 2, 'Saeta Rubia', NULL, ''),
	(11, 'Karl-Heinz', 'Rumenigge', 'Karl-Heinz Rummenigge (Lippstadt, 25 settembre 1955) è un dirigente sportivo ed ex calciatore tedesco, di ruolo attaccante, attuale membro del consiglio direttivo del Bayern Monaco. Con la Germania Ovest è stato campione d\'Europa nel 1980 e vicecampione del mondo nel 1982 e nel 1986. Soprannominato Kalle, è considerato uno dei più forti attaccanti della storia del calcio nonché come il migliore in assoluto durante la prima metà degli anni 1980. Occupa la 35ª posizione sia nella speciale classifica dei migliori calciatori del XX secolo pubblicata dalla rivista World Soccer che nell\'omonima classifica stilata dall\'IFFHS. Nel marzo del 2004, Pelé lo ha anche inserito nella FIFA 100, la lista dei 125 migliori calciatori viventi, redatta in occasione del Centenario della FIFA. Vincitore del Pallone d\'oro per due anni di seguito (1980 e 1981), è stato inserito in totale per otto anni di fila tra i candidati alla vittoria del premio arrivando a raggiungere anche la seconda posizione nel 1979. Nel 2022 è stato inoltre inserito nella Hall of Fame del calcio italiano.\r\nHa legato il suo nome a quello del Bayern Monaco, nel quale ha militato dal 1974 al 1984. Con la maglia dei bavaresi ha collezionato in totale 422 presenze e 217 reti, vincendo due campionati tedeschi, due Coppe nazionali, due Coppe dei Campioni e una Coppa Intercontinentale. Nel 1984 viene acquistato dall\'Inter, dove rimane fino al 1987, periodo in cui viene, per assonanza con il nome e doti sportive, soprannominato Il Kaiser ("imperatore" in tedesco). Ha chiuso la carriera nel 1989 tra le file del Servette. A livello individuale è stato inoltre per tre volte capocannoniere della Bundesliga[9] e per una volta della Coppa dei Campioni.Convocato in nazionale dal 1975 al 1986, ha partecipato a tre edizioni del campionato mondiale (Argentina 1978, Spagna 1982 e Messico 1986) e a due del campionato europeo (Italia 1980 e Francia 1984), vincendo l\'Europeo 1980. In nazionale ha giocato complessivamente 95 partite, segnando 45 reti.\r\nDopo il ritiro dall\'attività agonistica, è tornato al Bayern Monaco per intraprendere la carriera dirigenziale dapprima in veste di vicepresidente (dal 1991 al 2002) e in seguito in quella di amministratore delegato (fino al 2021). È stato presidente dell\'associazione dei club europei (ECA) dal 2008 al 2017 per poi diventarne il presidente onorario.', 'rummenigge2.jpg', 'rummenigge.png', 'si', 2, 'Der Kalle', NULL, NULL),
	(12, 'Johan', 'Cruyff', 'Johan Cruijff, all\'anagrafe Hendrik Johannes Cruijff (in olandese: [joːˈhɑn krœʏ̯f][2][3]; Amsterdam, 25 aprile 1947 – Barcellona, 24 marzo 2016), è stato un calciatore, allenatore di calcio e dirigente sportivo olandese, di ruolo attaccante o centrocampista.Considerato uno dei migliori giocatori nella storia del calcio, fu l\'interprete più emblematico del calcio totale con cui l\'Ajax e i Paesi Bassi di Rinus Michels rivoluzionarono la storia della disciplina tra la seconda metà degli anni 1960 e la prima metà degli anni 1970. Nel suo palmarès può vantare nove campionati olandesi, sei Coppe dei Paesi Bassi, un campionato spagnolo, una Coppa di Spagna, tre Coppe dei Campioni, una Supercoppa UEFA e una Coppa Intercontinentale. \r\nCon la nazionale olandese è stato finalista al campionato del mondo 1974 e terzo classificato al campionato d\'Europa 1976. Fra nazionale e squadre di club, segnò da professionista 402 gol in 716 partite ufficiali.\r\nNel corso della carriera ricevette vari soprannomi, tra cui Profeta del gol, che ispirò il titolo del film-documentario sulla vita sportiva di Cruijff diretto da Sandro Ciotti; il giornalista Gianni Brera lo ribattezzò invece il Pelé bianco.\r\nVincitore di tre Palloni d\'oro assegnati dalla rivista francese France Football (1971, 1973 e 1974.), occupa la 3ª posizione nella speciale classifica dei migliori calciatori del XX secolo pubblicata dalla rivista World Soccer alle spalle di Pelè e Maradona. Nel 2000 invece viene eletto secondo miglior calciatore del XX secolo, dietro al solo Pelé, nella classifica stilata dall\'IFFHS mentre nel mese di novembre del 2003, venne insignito del titolo onorifico di Golden Player dei Paesi Bassi come miglior giocatore olandese dell\'ultimo mezzo secolo (1954-2003). Nel marzo del 2004, Pelé lo ha anche inserito nella FIFA 100, la lista dei 125 migliori calciatori viventi, redatta in occasione del Centenario della FIFA.\r\nDa allenatore ha vinto due Coppe dei Paesi Bassi, una Coppa di Spagna, quattro campionati spagnoli, tre Supercoppe di Spagna, due Coppe delle Coppe, una Coppa dei Campioni e una Supercoppa UEFA. È uno dei sette allenatori che si sono aggiudicati la Coppa dei Campioni/Champions League dopo averla vinta da giocatore: detiene il record con Miguel Muñoz, Giovanni Trapattoni, Josep Guardiola, Frank Rijkaard, Carlo Ancelotti e Zinédine Zidane.\r\n\r\nNato nel quartiere Betondorp della periferia di Amsterdam, Johan era figlio di Manus Cruijff e Nel Draaijer i quali si erano trasferiti in via Tuinbouwstraat, dove avevano acquistato una abitazione popolare con un negozio di frutta e verdura, attività lavorativa della famiglia da alcune generazioni. Economicamente non benestanti, Johan e il fratello maggiore di due anni Heini passarono la maggior parte dell\'infanzia giocando partite di calcio con i bambini del quartiere; il piccolo Johan ebbe modo di evidenziare doti sorprendenti già a cinque anni di età. Appena dodicenne perse il padre per un attacco cardiaco; subito dopo la madre dovette cedere la casa e il negozio di prodotti ortofrutticoli. Johan ottenne per lei, dal vicepresidente dell\'Ajax, un posto come donna delle pulizie allo stadio e come commessa al banco del bar della società. Le conseguenti difficoltà economiche furono notevoli, e Johan lasciò gli studi per diventare calciatore. A causa dei suoi piedi piatti e della caviglia sformata, venne scartato dal servizio militare. \r\nIl 13 giugno 1967, al matrimonio del compagno di squadra Piet Keizer, Cruijff conobbe Diana Margaret "Danny" Coster, fotomodella e figlia del ricco commerciante di diamanti Cor Coster, che diventerà poi suo manager. Cruijff e Danny si sposarono il 2 dicembre 1968. Hanno avuto tre figli, Chantal (16 novembre 1970), Susila (27 gennaio 1972) e Jordi, chiamato così in onore di San Giorgio, patrono della Catalogna, nato nel periodo in cui il padre giocava per il Barcellona; il nome fu registrato così nei Paesi Bassi, poiché in Spagna vigeva ancora il franchismo che vietava nomi che ricordassero l\'Indipendentismo catalano. Dalla figlia Chantal, sposatasi con il portiere Jesús Angoy, Cruijff ha avuto il nipote Jessua Andrea, anche lui calciatore.\r\nIl 22 ottobre 2015 comunicò di avere un tumore polmonare, che lo porterà alla morte cinque mesi dopo, il 24 marzo 2016 a Barcellona, all\'età di sessantotto anni.', 'cruyff2.jpg', 'cruyff.jpg', 'si', 3, 'Profeta del gol', NULL, NULL);

